const fetchItems=async()=>{try{const response=await fetch("http://localhost:3000/api/products");return response.json()}catch(error){alert("Nous rencontrons actuellement des difficultés pour l'affichage de nos articles.\nMerci de réessayer ultèrieurement.\nVeuillez nous excuser pour la gêne occasionnée.")}},itemsDisplay=async()=>{const itemsData=await fetchItems();for(const item of itemsData){const image=itemImageDisplay(item.imageUrl,item.altTxt),nameOfItem=itemNameDisplay(item.name),description=itemDescriptionDisplay(item.description),article=itemArticleDisplay(image,nameOfItem,description),link=itemLinkDisplay(item._id,article);itemContainerSelect(link)}},itemLinkDisplay=(itemId,article)=>{const link=document.createElement("a");return link.setAttribute("href","./product.html?id="+itemId),link.appendChild(article),link},itemArticleDisplay=(image,nameOfItem,description)=>{const article=document.createElement("article");return article.appendChild(image),article.appendChild(nameOfItem),article.appendChild(description),article},itemImageDisplay=(itemImageUrl,itemAltTxt)=>{const image=document.createElement("img");return image.setAttribute("src",itemImageUrl),image.setAttribute("alt",itemAltTxt),image},itemNameDisplay=itemName=>{const nameOfItem=document.createElement("h3");return nameOfItem.classList.add("productName"),nameOfItem.textContent=itemName,nameOfItem},itemDescriptionDisplay=itemDescription=>{const description=document.createElement("p");return description.classList.add("productDescription"),description.textContent=itemDescription,description},itemContainerSelect=link=>{const section=document.getElementById("items").appendChild(link);return section};itemsDisplay();